    id: 'mock-subscription',
    callback: vi.fn(),
    unsubscribe: vi.fn(),
  }

  beforeEach(() => {
    vi.clearAllMocks()

    // Default mock: no session
    vi.mocked(supabase.auth.getSession).mockResolvedValue({
      data: { session: null },
      error: null,
    })

    vi.mocked(supabase.auth.onAuthStateChange).mockReturnValue({
      data: { subscription: mockSubscription },
    })
  })

