      await waitFor(() => {
        expect(result.current.isLoading).toBe(false)
      })

      expect(authApi.fetchProfile).toHaveBeenCalledWith('user-123')
      expect(result.current.user).toEqual(mockUser)
      expect(result.current.isAuthenticated).toBe(true)
